settings.custom_css:
.tittle { 
    font-size: 38px; 
    font-weight: 600; 
    
    /* Opcional: Adicione line-height para melhor legibilidade */
    /* line-height: 1.2; */
} 

/* Tablet - telas até 768px */
@media (max-width: 768px) {
    .tittle {
        font-size: 32px; /* Reduz um pouco o tamanho */
    }
}

/* Smartphone - telas até 480px */
@media (max-width: 480px) {
    .tittle {
        font-size: 22px !important; /* Tamanho menor para telas pequenas */
        
        /* Opcional: Ajuste o peso da fonte se necessário */
        /* font-weight: 500; */
    }
}
